import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CVEAnalysis = () => {
  const [selectedCVE, setSelectedCVE] = useState('');
  const [nvdAnalysis, setNvdAnalysis] = useState(null);
  const [redhatAnalysis, setRedhatAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (selectedCVE) {
      fetchNvdAnalysis(selectedCVE);
      fetchRedhatAnalysis(selectedCVE);
    }
  }, [selectedCVE]);

  const handleCVESelection = (event) => {
    const cve = event.target.value;
    setSelectedCVE(cve);
  };

  const fetchNvdAnalysis = (cve) => {
    setLoading(true);
    axios.get(`https://services.nvd.nist.gov/rest/json/cves/1.0?cveId=${cve}`)
      .then(response => {
        setNvdAnalysis(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching NVD analysis:', error);
        setLoading(false);
      });
  };

  const fetchRedhatAnalysis = (cve) => {
    setLoading(true);
    // Adjust the URL to the Red Hat API endpoint
    axios.get(`https://access.redhat.com/hydra/rest/securitydata/cve/${cve}.json`)
      .then(response => {
        setRedhatAnalysis(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching Red Hat analysis:', error);
        setLoading(false);
      });
  };

  return (
    <div>
      <select onChange={handleCVESelection}>
        <option value="">Select a CVE</option>
        <option value="CVE-2009-5155">CVE-2009-5155</option>
        {/* Add other CVE options here */}
      </select>

      {loading && <p>Loading...</p>}
      {nvdAnalysis && (
        <div>
          <h2>NVD Analysis:</h2>
          {/* Displaying the NVD analysis in a readable format */}
          <p>CVE ID: {nvdAnalysis.CVE_data_meta.ID}</p>
          <p>Description: {nvdAnalysis.CVE_Items[0].cve.description.description_data[0].value}</p>
          <p>References:</p>
          <ul>
            {nvdAnalysis.CVE_Items[0].cve.references.reference_data.map(reference => (
              <li key={reference.url}>{reference.url}</li>
            ))}
          </ul>
        </div>
      )}

      {redhatAnalysis && (
        <div>
          <h2>Red Hat Analysis:</h2>
          {/* Display Red Hat analysis data */}
          {/* You need to adjust this part based on the structure of the Red Hat API response */}
          {/* For example: */}
          <p>Severity: {redhatAnalysis.severity}</p>
          <p>Description: {redhatAnalysis.description}</p>
          {/* Add other relevant fields */}
        </div>
      )}
    </div>
  );
};

export default CVEAnalysis;
