import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [selectedCVE, setSelectedCVE] = useState('');
  const [nvdAnalysis, setNvdAnalysis] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleCVESelection = (event) => {
    const cve = event.target.value;
    setSelectedCVE(cve);

    // Fetch NVD analysis
    fetchNvdAnalysis(cve);
  };

  const fetchNvdAnalysis = (cve) => {
    setLoading(true);
    axios.get(`https://services.nvd.nist.gov/rest/json/cve/1.0/${cve}`)
      .then(response => {
        setNvdAnalysis(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching NVD analysis:', error);
        setLoading(false);
      });
  };

  return (
    <div>
      <h1>NVD Analysis Application</h1>

      <select onChange={handleCVESelection}>
        <option value="">Select a CVE</option>
        <option value="CVE-2023-39325">CVE-2023-39325</option>
        {/* Add other CVE options here */}
      </select>

      {loading && <p>Loading...</p>}

      {nvdAnalysis && (
        <div>
          <h2>NVD Analysis:</h2>
          <p>{JSON.stringify(nvdAnalysis)}</p>
        </div>
      )}
    </div>
  );
};

export default App;
